<?php /*  CONTEM CODIGOS OBRIGATORIOS A TODAS AS PAGINAS DO SITE *///ini_set( "display_errors", 0); //desabilita os erros$compressao_zlib = false;$compressao_gzip = false;try{	ini_set('zlib.output_compression', 'On');	ini_set('zlib.output_compression_level', 1);    $compressao_zlib = true;}catch (Exception $e){	$compressao_zlib = false;}if($compressao_zlib == false){	try{		if (substr_count($_SERVER['HTTP_ACCEPT_ENCODING'], 'gzip')){			ob_start('ob_gzhandler');			$compressao_gzip = true;		}	}	catch (Exception $e){		$compressao_gzip = false;	}}//ini_set( "display_errors", 1); //habilita os erros?>