<?php /*  CONTEM CODIGOS OBRIGATORIOS A TODAS AS PAGINAS DO SITE *///ini_set( "display_errors", 0); //desabilita os erros$compressao_zlib = false;$compressao_gzip = false;$erro = 'sem erro';try{	ini_set('zlib.output_compression', 'On'); //era 'On' no lugar do 0	ini_set('zlib.output_compression_level', 1);    $compressao_zlib = true;}catch (Exception $e){	$compressao_zlib = false;	$erro = 'erro zlib';}if($compressao_zlib == false){	try{		ob_start("ob_gzhandler");		$compressao_gzip = true;	}	catch (Exception $e){		$compressao_gzip = false;		$erro .= 'erro gzip';	}}//ini_set( "display_errors", 1); //habilita os erros?>